class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0, c = 0, c02 = 0;
        for(int i = 0; i < n/2; i++) {
            for(int j = 0; j < m/2; j++) {
                int sum = (grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j]);
                ans += min( sum,  4 - sum );
            }
        }
        cout << ans << endl;
        if((n & 1) && (m & 1) ) {
            if(grid[n/2][m/2] == 1) ans++;
        } 
        if(n & 1) {
            cout << ans << endl;
            for(int j = 0; j < m/2; j++) {
                if(grid[n/2][j] != grid[n/2][m - 1 - j]) {
                    ans++, c02++;
                } else if(grid[n/2][j] == 1) c+=2;
            }
        } 
        if(m & 1) {
            for(int i = 0; i < n/2; i++) {
                if(grid[i][m/2] != grid[n- 1 - i][m/2]) {
                    ans++, c02++;
                } else if(grid[i][m/2] == 1) c+=2;
            }
        }
        cout << ans;
        if(c % 4 != 0 && c02 == 0) {
            ans += 2;
        } 
        return ans;
    }
};
