class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int res = 0;
        vector<int> v;
        bool ok1 = 0, ok2 = 0;
        int s = 0;
        for(int i = 0; i < (n + 1) / 2; i ++ ){
            for(int j = 0; j < (m + 1) / 2; j ++ ){
                int c[2] = {0};
                c[a[i][j]] ++;
                if(n - i - 1 != i && m - j - 1 != j){
                    c[a[n - i - 1][j]] ++;
                    c[a[n - i - 1][m - j - 1]] ++;
                    c[a[i][m - j - 1]] ++;
                }
                else if(n - i - 1 != i){
                    c[a[n - i - 1][j]] ++;
                }
                else if(m - j - 1 != j){
                    c[a[i][m - j - 1]] ++;
                }
                if(c[0] + c[1] == 2 && c[0] == 1){
                    ok1 = 1;
                }
                res += min(c[0], c[1]);
                if(c[1] > c[0]){
                    s += c[0] + c[1];
                }
            }
        }
        if(s & 1){
            res ++;
            if(a[n / 2][m / 2]) s --;
            else s ++;
        }
        if(s % 4 == 2){
            if(!ok1) res += 2;
        }
        return res;
    }
};
