class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),g=0,n1=(n)/2,m1=(m)/2;
        for (int i=0;i<n1;i++){
            for (int j=0;j<m1;j++){
                int x=grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                g+=min(4-x,x);
            }
        }
        int yi=0,gg=0,ling=0;
        if (n%2){
            for (int i=0;i<m1;i++) {
                if (grid[n/2][i]!=grid[n/2][m-i-1]) {
                    g++;
                    yi++;
                    gg++;
                }else if (grid[n/2][i]==1) yi++;
                else ling++;
            }
        }if (m%2){
            for (int i=0;i<n1;i++){
                if (grid[i][m/2]!=grid[n-i-1][m/2]) {
                    g++,yi++,gg++;
                }else if (grid[i][m/2]==1) yi++;
                else ling++;
            }
        }
        if (n%2&&m%2&&grid[n/2][m/2]) g++;
        if (yi%2&&!gg) g+=2;
        return g;
    }
};
