class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size();
        int m = grid[0].size();
        for(int i = 0; i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int c0=0,c1=0;

                if(grid[i][j] == 0)c0++;
                else c1++;

                if(grid[i][m-j-1] == 0)c0++;
                else c1++;

                if(grid[n-i-1][j] == 0)c0++;
                else c1++;

                if(grid[n-i-1][m-j-1] == 0)c0++;
                else c1++;

                ans+=min(c0,c1);
            }
        }

        if(m%2 && n%2 && grid[n/2][m/2]==1)ans++;
        int c0=0,c1=0,c2=0;
        if(m%2){

            //c0 not equal
            //c1 equal ane 1
            //c2 equal and 0
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-i-1][m/2])c0++;
                else if(grid[i][m/2])c1++;
                else c2++;
            }
        }
        if(n%2){
            // int c0=0,c1=0,c2=0;
            //c0 not equal
            //c1 equal ane 1
            //c2 equal and 0
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i] != grid[n/2][m-i-1])c0++;
                else if(grid[n/2][i])c1++;
                else c2++;
            }
        }         
            c1%=2;
            if(c1){
                if(c0>=1){
                    c0-=1;
                    ans++;
                }
                else ans+=2;
            }
            ans+=c0;
        return ans;
    }
};
