map0 = [0,1,2,1,0]
class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        out = 0
        debug = 0
        cnt2 = [0,0,0]
        m,n = len(grid),len(grid[0])
        temp = [[0 for _ in range(n)] for _ in range(m)]
        for i in range(m):
            for j in range(n):
                if temp[i][j]:
                    continue
                ii = m-1-i
                jj = n-1-j
                points = set([(i,j),(ii,j),(ii,jj),(i,jj)])
                lp = len(points)
                if lp == 1:
                    x,y = points.pop()
                    temp[x][y] = 1
                    if grid[x][y]:
                        out += 1
                elif lp == 2:
                    v = 0
                    for x,y in points:
                        temp[x][y] = 1
                        v += grid[x][y]
                    cnt2[v] += 1
                else:
                    v = 0
                    for x,y in points:
                        temp[x][y] = 1
                        v += grid[x][y]
                    out += map0[v]
        if debug:
            print(cnt2,out,temp)
        z = cnt2[2]*2
        z = z%4
        if not z:
            out += cnt2[1]
        else:
            if cnt2[1]:
                out += cnt2[1]
            else:
                out += 2
        return out
