class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int cnt = 0;
        int n = grid.size();
        int m = grid[0].size();
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt0 = 0, cnt1 = 0;
                if(grid[i][j] == 0){
                    cnt0++;
                }
                else cnt1++;
                if(grid[n-1-i][j] == 0) cnt0++;
                else cnt1++;
                if(grid[i][m-1-j] == 0) cnt0++;
                else cnt1++;
                if(grid[n-1-i][m-1-j] == 0) cnt0++;
                else cnt1++;
                if(cnt0 == 4 || cnt1 == 4) continue;
                else if(cnt0 > cnt1){
                    cnt += (cnt1);
                }
                else{
                    cnt += (cnt0);
                }
            }
        }
        int midRow = 0, cntMidRow0 = 0, cntMidRow1 = 0;
        if(n%2){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j] != grid[n/2][m-1-j]){
                    midRow++;
                }
                else{
                    if(grid[n/2][j] == 1){
                        cntMidRow1 += 2;
                    }
                    else{
                        cntMidRow0 += 2;
                    }
                }
            }
        }
        int midCol = 0;
        int cntMidCol0 = 0, cntMidCol1 = 0;
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-1-i][m/2]) midCol++;
                else{
                    if(grid[i][m/2] == 1){
                        cntMidCol1 += 2;
                    }
                    else cntMidCol0 += 2;
                }
            }
        }
        if(m%2 && n%2){
            if(grid[n/2][m/2] == 1){
                cnt++;
            }
        }
        if((cntMidRow1+cntMidCol1)%4 == 0){
            cnt += (midCol + midRow);
            return cnt;
        }
        else{
            if(midCol >= 1 || midRow >= 1){
                cnt += (midCol + midRow);
                return cnt;
            }
            cnt += (2);
            return cnt;
        }
        return cnt;
    }
};
