class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        ret = 0

        ro = m % 2
        co = n % 2
        tones = 0
        fones = 0
        l2 = 0
        l1 = 0

        for i in range(m // 2 + ro):
            for j in range(n // 2 + co):
                ones = 0
                dif = 1
                if grid[i][j] == 1:
                    ones += 1
                if grid[m - 1 - i][j] == 1 and m - 1 - i != i:
                    ones += 1
                if m - 1 - i != i:
                    dif += 1
                if grid[i][n - 1 - j] == 1 and j != n - 1 - j:
                    ones += 1
                if j != n - 1 - j:
                    dif += 1
                if grid[m - 1 - i][n - 1 - j] == 1 and m - 1 - i != i and n - 1 - j != j:
                    ones += 1
                if m - 1 - i != i and n - 1 - j != j:
                    dif += 1

                if dif == 2 and ones == 1:
                    l2 = 1
                if dif == 1 and ones == 1:
                    l1 = 1

                if ones >= 1:
                    fones += dif
                ret += min(ones, dif - ones)

                tones += ones


        if co == 0 and ro == 0 and tones % 4 != 0:
            return ret
        if fones % 4 == 0:
            return ret
        if fones % 4 == 1:
            return ret + 1
        if fones % 4 == 2:
            if l2 == 1:
                return ret
            return ret + 2
        if fones % 4 == 3:
            if l2 == 1:
                return ret + 1
            return ret + 3
