class Solution {
	public int minFlips(int[][] grid) {
		int m = grid.length, n = grid[0].length;
		int cnt = 0;
		for (int i = 0; i < m / 2; i++) {
			for (int j = 0; j < n / 2; j++) {
				int cnt1 = 0;
				if (grid[i][j] == 1) cnt1++;
				if (grid[m - 1 - i][j] == 1) cnt1++;
				if (grid[i][n - 1 - j] == 1) cnt1++;
				if (grid[m - 1 - i][n - 1 - j] == 1) cnt1++;
				switch (cnt1) {
					case 1, 3:
						cnt++;
						break;
					case 2:
						cnt += 2;
						break;
					default:
						break;
				}
			}
		}
		int cnt1 = 0, cntD = 0;
		if (n % 2 == 1 && m % 2 == 1) {
			cnt1 += grid[m / 2][n / 2];
		}
		if (n % 2 == 1) {
			for (int i = 0; i < m / 2; i++) {
				if (grid[i][n / 2] != grid[m - 1 - i][n / 2]) {
					cntD++;
				} else if (grid[i][n / 2] == 1) {
					cnt1 += 2;
				}
			}
		}
		if (m % 2 == 1) {
			for (int i = 0; i < n / 2; i++) {
				if (grid[m / 2][i] != grid[m / 2][n - 1 - i]) {
					cntD++;
				} else if (grid[m / 2][i] == 1) {
					cnt1 += 2;
				}
			}
		}
		if (cnt1 % 4 == 0) return cnt + cntD;
		if (cnt1 % 2 == 0) return cntD > 0 ? cnt + cntD : cnt + 2 + cntD;
		if (cnt1 % 4 == 1) return cnt + cntD + 1;
		return cntD > 0 ? cnt + cntD + 1 : cnt + 3 + cntD;
	}
}
