class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int k=grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                ans+=min(k,4-k);
            }
        }
        ans+=((n&1)&&(m&1)&&(grid[n/2][m/2]==1));
        int g=0,one=0;
        if(n&1){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1])g++;
                else if(grid[n/2][j]==1)one++;
            }
        }
        if(m&1){
            for(int j=0;j<n/2;j++){
                if(grid[j][m/2]!=grid[n-j-1][m/2])g++;
                else if(grid[j][m/2]==1)one++;
            }
        }
        ans+=g;
        if(!g&&(one&1))ans+=2;
        return ans;
    }
};
