class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        tot = 0
        res = 0
        a = b = c = 0
        for i in range((n + 1) // 2):
            for j in range((m + 1) // 2):
                x1, y1 = i, j
                x2, y2 = i, m - 1 - j
                x3, y3 = n - 1 - i, j
                x4, y4 = n - 1 - i, m - 1 - j 
                t = set([(x1, y1), (x2, y2), (x3, y3), (x4, y4)])
                if len(t) == 1:
                    if grid[x1][y1] == 1:
                        res += 1 
                elif len(t) == 2:
                    cnt1 = cnt0 = 0
                    for x, y in t:
                        if grid[x][y] == 0: cnt0 += 1 
                        else: cnt1 += 1 
                        tot += grid[x][y]

                    if cnt0 and cnt1:
                        res += 1 
                        a += 1 
                    elif cnt1 == 2:
                        b += 1 
                    elif cnt0 == 2:
                        c += 1 

                elif len(t) == 4:
                    cnt1 = cnt0 = 0
                    for x, y in t:
                        if grid[x][y] == 0: cnt0 += 1 
                        else: cnt1 += 1
                    if cnt1 > cnt0: res += cnt0
                    else: res += cnt1
        if tot % 4 == 2:
            if a >= 2: 
                pass
            else:
                res += 2
        return res
