class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int ans = 0;
        int n = g.size(), m = g[0].size();
        for (int i = 0; i < n - 1 - i; i++)
            for (int j = 0; j < m - 1 - j; j++) {
                int x = g[i][j] + g[n - 1 - i][j] + g[i][m - 1 - j] + g[n - 1 - i][m - 1 - j];
                ans += min(x, 4 - x);
            }
        int aa = 0, bb = 0;
        if (n % 2 == 1) {
            if (m % 2 == 1)
                ans += g[n / 2][m / 2];
            for (int j = 0; j < m - 1 - j; j++)
                if (g[n / 2][j] != g[n / 2][m - 1 - j])
                    aa++;
                else if (g[n / 2][j])
                    bb++;
        }
        if (m % 2 == 1)
            for (int i = 0; i < n - 1 - i; i++)
                if (g[i][m / 2] != g[n - 1 - i][m / 2])
                    aa++;
                else if (g[i][m / 2])
                    bb++;
        ans += aa;
        if (bb % 2 == 1 && aa == 0)
            ans += 2;
        return ans;
    }
};
