class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = (n%2)*(m%2)*grid[n/2][m/2];
        for(int i = 0; i < n/2; i++) {
            for(int j = 0; j < m/2; j++) {
                int cnt = 0;
                if(grid[i][j]) cnt++;
                if(grid[n-i-1][j]) cnt++;
                if(grid[i][m-j-1]) cnt++;
                if(grid[n-i-1][m-j-1]) cnt++;
                ans += min(cnt,4-cnt);
            }
        }
        vector<int> v;
        if(n%2) for(int j = 0; j < m/2; j++) {
            if(grid[(n/2)][j]&&grid[(n/2)][m-j-1]) v.push_back(0);
            else if(grid[(n/2)][j]||grid[(n/2)][m-j-1]) v.push_back(1);
            else v.push_back(2);
        }
        if(m%2) for(int i = 0; i < n/2; i++) {
            if(grid[i][(m/2)]&&grid[n-i-1][(m/2)]) v.push_back(0);
            else if(grid[i][(m/2)]||grid[n-i-1][(m/2)]) v.push_back(1);
            else v.push_back(2);
        }
        sort(v.begin(),v.end());
        int x = v.size()-1;
        for(int i = 0; i < x; i+=2) {
            if(v[i]==2) break;
            ans += min(v[i]+v[i+1],4-v[i]-v[i+1]);
            // if(v[i+1]==2&&v[i]==0) ans-=2;
        }
        if(v.size()%2) ans += (2-v.back());
        return ans;
    }
};
