class Solution {
public:

    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid.front().size();
        int cost=0;
        int ones=0;
        bool eq=false;
        for(int i=0;i<(n+1)/2;i++){
            for(int j=0;j<(m+1)/2;j++){
                set<pair<int,int>> st={
                    {i,j},
                    {i,m-j-1},
                    {n-i-1,j},
                    {n-i-1,m-j-1}
                };
                int one=0;
                for(auto & pr:st){
                    one+=grid[pr.first][pr.second];
                }
                int zero=st.size()-one;
                if(st.size()==1){
                    cost+=one;
                }else{
                    if(st.size()==2){
                      if(one==zero){
                          eq=true;
                      }      
                    }

                    int diff=min(zero,one);
                    if(one>=zero){
                        ones+=st.size();
                    }
                    // cout<<i<<" , "<<j<<" : "<<diff<<endl;
                    cost+=diff;    
                }
            }
        }
        int mod=ones%4;
        // cout<<"org cost : "<<cost<<endl;
        // cout<<"ones : "<<ones<<", mod : "<<mod<<endl;
        // cout<<"eq : "<<eq<<endl;
        if(mod != 0 && mod != 2){
            cout<<"something wrong here"<<endl;
            cout<<"org cost : "<<cost<<endl;
            cout<<"ones : "<<ones<<", mod : "<<mod<<endl;
            cout<<"eq : "<<eq<<endl;
        }
        if(false == eq) cost+=mod;
        // cout<<"cost : "<<cost<<endl;
        return cost;
    }
};
