class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int count = 0, minm = 0, maxm = 0;
        for(int i = 0; i < n / 2 + (n % 2); ++i) {
            for(int j = 0; j < m / 2 + (m % 2); ++j) {
                int sum = grid[i][j] + grid[i][m - j - 1] + grid[n - i - 1][j] + grid[n - i - 1][m - j - 1];
                if(i == n - i - 1 && j == m - j - 1){
                    sum = grid[i][j];
                    count += sum;
                }else if(j == m - j - 1) {
                    sum = grid[i][j] + grid[n - i - 1][j];
                    count += (sum % 2);
                    if(1) {
                        if(sum == 1) {
                            minm -= 2;
                            minm = max(0, minm);
                            maxm += 2;
                        } else if(grid[i][j]) {
                            minm += 2;
                            maxm += 2;
                        }
                    }
                } else if(i == n - i - 1) {
                    sum = grid[i][j] + grid[i][m - j - 1];
                    count += (sum % 2);
                    if(1) {
                        if(sum == 1) {
                            minm -= 2;
                            minm = max(0, minm);
                            maxm += 2;
                        } else  if(grid[i][j]) {
                            minm += 2;
                            maxm += 2;
                        }
                    }
                } else {
                    count += min(sum, 4 - sum);
                }
            }
        }

        for(int i = minm; i <= maxm; ++i) {
            if(i % 4 == 0) {
                return count;
            }
        }

        return count + 2;
    }
};
