class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ret = 0;
        for(int i = 0; i < n / 2; i++){
            for(int j = 0; j < m /2; j++){
                int now = 0;
                if(grid[i][j] == 0) now++;
                if(grid[i][m - 1 - j] == 0) now++;
                if(grid[n - 1 - i][j] == 0) now++;
                if(grid[n - 1 - i][m - 1 - j] == 0) now++;
                ret += min(4 - now, now);
            }
        }
        int cnt_ = 0, cnt4 = 0;
        if(n & 1){
            int l = 0, r = m - 1;
            while(l < r){
                if(grid[n / 2][l] != grid[n / 2][r]) cnt_++;
                else if(grid[n / 2][l] == 1) cnt4 += 2;
                l++, r--;
            }
        }
        if(m & 1){
            int l = 0, r = n - 1;
            while(l < r){
                if(grid[l][m / 2] != grid[r][m / 2]) cnt_+=1;
                else if(grid[l][m / 2] == 1) cnt4 += 2;
                l++, r--;
            }
        }

        if(n & 1) if(m & 1) if(grid[n / 2][m / 2] == 1) cnt4++;
        ret += cnt_;

        if(cnt4 % 4 == 0) return ret;
        if(cnt4 % 4 == 1) return ret + 1;
        if(cnt4 % 4 == 2){
            if(cnt_) return ret;
            else return ret + 2;
        }
        if(cnt_) return ret + 1;
        else return ret + 3;
    }
};
