public class Solution {
    public int MinFlips(int[][] grid) {
        int m = grid.Length, n = grid[0].Length;
        int ans = 0;
        for (int i = 0; i < m / 2; ++i) for (int j = 0; j < n / 2; ++j) {
            int v = grid[i][j] + grid[m-i-1][j] + grid[i][n-j-1] + grid[m-i-1][n-j-1];
            ans += Math.Min(v, 4-v);
        }
        if (m % 2 == 1 && n % 2 == 1 && grid[m/2][n/2] == 1) { grid[m/2][n/2] = 0; ++ans; }
        int c = 0, one = 0, t = 0;
        if (m % 2 == 1) {
            for (int j = 0; j < n / 2; ++j) {
                if (grid[m/2][j] != grid[m/2][n-j-1]) ++one;
                c += grid[m/2][j];
                ++t;
            }
        }
        if (n % 2 == 1) {
            for (int i = 0; i < m / 2; ++i) {
                if (grid[i][n/2] != grid[m-i-1][n/2]) ++one;
                c += grid[i][n/2];
                ++t;
            }
        }
        if (one == 0 && c % 2 == 1) ans += 2;
        else if (t <= 1) ans += Math.Max(one, c);
        else ans += one;
        return ans;
    }
}
