#define rep(i,a,n) for(int i=a;i<=n;i++)
#define per(i,a,n) for(int i=n;i>=a;i--)
#define pb push_back
#define SZ(v) ((int)v.size())
#define fs first
#define sc second
#define all(x) (x.begin()),(x.end())
typedef long long ll;
typedef double db;
typedef pair<int,int> pii;

class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n=SZ(a),m=SZ(a[0]),ans=0;
        vector<int> er={0,0,0};
        rep(i,0,n-1){
        	rep(j,0,m-1){
        		int ii=n-1-i,jj=m-1-j;
        		if(ii<i||jj<j)	continue;
        		set<pii> st;
        		st.insert({i,j});
        		st.insert({i,jj});
        		st.insert({ii,j});
        		st.insert({ii,jj});
        		int sum=0;
        		for(auto [x,y]:st){
        			sum+=a[x][y];
				}
        		if(SZ(st)==4){
        			ans+=min(abs(4-sum),sum);
				}
				else if(SZ(st)==2){
					er[sum]++;
					if(sum==1)	ans++;
				}
				else{
					assert(SZ(st)==1);
					if(sum==1)	ans++;
				}
			}
		}
		if(er[2]%2&&er[1]==0)	ans+=2;
		return ans;
    }
};
