class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0; 
        int n = grid.size(), m = grid[0].size();

        if(n%2 && m%2) {
            if(grid[n/2][m/2]==1) ans+=1;
        }

        vector<int> v(3);
        if(n%2) {
            int l = 0, r = m-1;
            while(l<r) {
                int cur = grid[n/2][l] + grid[n/2][r];
                v[cur]++;
                l++;
                r--;
            }
        }

        if(m%2) {
            int l = 0, r = n-1;
            while(l<r) {
                int cur = grid[l][m/2] + grid[r][m/2];
                v[cur]++;
                l++;
                r--;
            }
        }

        if(v[2]%2) {
            if(v[1]>0) ans+=v[1];
            else ans+=2;
        } else ans+=v[1];

        for(int i=0; i<n/2; i++) {
            for(int j=0; j<m/2; j++) {
                int cur = grid[i][j]+grid[n-1-i][j]+grid[i][m-1-j]+grid[n-1-i][m-1-j];

                if(cur!=0 && cur!=4) ans+=min(cur, 4-cur);
            }
        }

        return ans;
    }
};
