class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int ans = 0 ; 
        int n = grid.size() , m = grid[0].size() ; 
        int vis[n][m] ;
        memset(vis,0,sizeof(vis));
        int two_one = 0 , mix = 0; 
        for(int i = 0 ; i<n; ++i)
        {
            for(int j = 0 ; j<m; ++j)
            {
                if(vis[i][j]==1)
                    continue ;

                set<pair<int,int>> st;
                st.insert({i,j});

                st.insert({n-i-1,j}); // bottom left
                st.insert({i,m-j-1}); // right
                st.insert({n-i-1,m-j-1}); 

                int o = 0 , z = 0 ; 

                for(auto & p : st)
                {
                    int x = p.first , y = p.second ; 
                    vis[x][y] = 1 ; 
                    if(grid[x][y]==1)o++;
                    else z++ ; 
                }
                if(st.size()==4)
                {
                    ans+= min(o,z) ; 
                    continue ; 
                }
                if(st.size()==1)
                {
                    ans+= o ;
                    continue ; 
                }
                if(st.size()==2)
                {
                    if(z==0)
                        two_one++ ; 
                    else if(z==1)
                        mix++ ; 
                }
            }
        }
        ans+=mix ; 
        if(two_one%2!=0)
        {
            if(mix==0)
                ans+= 2 ; 

        }
        return  ans ; 

    }
};
