class Solution {
public:
    int minFlips(vector<vector<int>> &grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0, cnt = 0, one2 = 0;

        for (int i = 0; i < n - i - 1; ++i) {
            for (int j = 0; j < m - j - 1; ++j) {
                int one = grid[i][j] + grid[n - i - 1][j] + grid[i][m - j - 1] + grid[n - i - 1][m - j - 1];
                ans += min(one, 4 - one);
            }
        }

        if (n % 2 == 1) {
            for (int j = 0; j < m - j - 1; ++j) {
                int one = grid[n / 2][j] + grid[n / 2][m - j - 1];

                if (one % 2 == 1) {
                    ++ans;
                    ++cnt;
                } else {
                    one2 += one;
                }
            }
        }

        if (m % 2 == 1) {
            for (int i = 0; i < n - i - 1; ++i) {
                int one = grid[i][m / 2] + grid[n - i - 1][m / 2];

                if (one % 2 == 1) {
                    ++ans;
                    ++cnt;
                } else {
                    one2 += one;
                }
            }
        }

        if (n % 2 == 1 && m % 2 == 1) {
            one2 += grid[n / 2][m / 2];
        }

        if (one2 % 4 == 0)
            return ans;

        if (one2 % 4 == 1) {
            return ans + 1;
        }

        if (one2 % 4 == 3) {
            if (cnt > 0)
                return ans + 1;

            return ans + 3;
        }

        if (cnt > 0)
            return ans;

        return ans + 2;
    }
};
