class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n=grid.size(),m=grid[0].size();
        for(int i=0;i<n/2;++i){
            for(int j=0;j<m/2;++j){
                int c1 = 0, c0=0;
                if(grid[i][j]) ++c1;
                else ++c0;
                int nx = i, ny = m-1-j;
                if(grid[nx][ny]) ++c1;
                else ++c0;
                nx = n-i-1,ny = m-1-j;
                if(grid[nx][ny]) ++c1;
                else ++c0;
                nx = n-1-i;
                ny = j;
                if(grid[nx][ny]) ++c1;
                else ++c0;
                ans += min(c1,c0);

            }
        }
        int f=0;
        int ones = 0;
        if(n%2==1){
            for(int j=0;j<m/2;++j){
                if(grid[n/2][j]!=grid[n/2][m-1-j]) ++f;
                else if(grid[n/2][j]==1) ++ones;
            }
        }
        if(m%2==1){
            for(int i=0;i<n/2;++i){
                if(grid[i][m/2]!=grid[n-1-i][m/2]) ++f;
                else if(grid[i][m/2]) ++ones;
            }
        }
        if(n%2==1 and m%2==1){
            if(grid[n/2][m/2]) ++ans;
        }
        if(ones%2==1 and f==0){
            ++ans;
            ++ans;
        }
        else{
            ans += f;
        }
        return ans;

    }  
};
