class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),ans=0;
        for(int i=0;i<n/2;i++) {
            for(int j=0;j<m/2;j++) {
                int ones=grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                ans+=min(ones,4-ones);
            }
        }
        if(n%2 && m%2) {
            // cout << "Came here1\n";
            ans+=grid[n/2][m/2];
            int p=0,c=0;
            for(int i=0;i<n/2;i++) {
                if(grid[i][m/2]==grid[n-i-1][m/2]) p+=grid[i][m/2];
                else c++;
            }
            for(int j=0;j<m/2;j++) {
                if(grid[n/2][j]==grid[n/2][m-j-1]) p+=grid[n/2][j];
                else c++;
            }
            if(p%2==0) {
                ans+=c;
            } else {
                if(c==0) ans+=2;
                else ans+=c;
            }
            return ans;
        }
        if(n%2) {
            // cout << "Came here2\n";
            int p=0,c=0;
            for(int j=0;j<m/2;j++) {
                if(grid[n/2][j]==grid[n/2][m-j-1]) p+=grid[n/2][j];
                else c++;
            }
            if(p%2==0) {
                ans+=c;
            } else {
                if(c==0) ans+=2;
                else ans+=c;
            }
            return ans;
        }
        if(m%2) {
            // cout << "Came here3\n";
            int p=0,c=0;
            for(int i=0;i<n/2;i++) {
                if(grid[i][m/2]==grid[n-i-1][m/2]) p+=grid[i][m/2];
                else c++;
            }
            // cout << p << " " << c << "\n";

            if(p%2==0) {
                ans+=c;
            } else {
                if(c==0) ans+=2;
                else ans+=c;
            }
            return ans;
        }
        // cout << "Came here4\n";
        return ans;
    }
};
