class Solution:
    def minFlips(self, g: List[List[int]]) -> int:
        ans = 0
        n = len(g)
        m = len(g[0])

        if n & 1 and m & 1 and g[n//2][m//2]:
            ans += 1
            g[n//2][m//2] = 0

        for i in range(n//2):
            for j in range(m//2):
                c1 = g[i][j]
                c1 += g[-i-1][j]
                c1 += g[i][-j-1]
                c1 += g[-i-1][-j-1]
                ans += min(c1, 4-c1)

        extra = 0
        add = 0
        if n & 1:
            for j in range(m//2):
                if g[n//2][j] != g[n//2][-j-1]:
                    add += 1
                else:
                    extra += (g[n//2][j] == 1) * 2

        if m & 1:
            for i in range(n//2):
                if g[i][m//2] != g[-i-1][m//2]:
                    add += 1
                else:
                    extra += (g[i][m//2] == 1) * 2

        if extra % 4 and add == 0:
            ans += 2
        return ans + add
