class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])
        ans = 0
        for i in range(n//2):
            for j in range(m//2):
                if (i == n-1-i) or (j == m-1-j): 
                    continue
                # print('')
                cnt = grid[i][j]
                cnt += grid[n-1-i][j]
                cnt += grid[i][m-1-j]
                cnt += grid[n-1-i][m-1-j]
                # print(i, j, 'cnt = ', cnt)
                if cnt == 1 or cnt == 3:
                    ans += 1
                elif cnt == 2:
                    ans += 2
        # print('half', ans)       
        if n % 2 == 1 and m % 2 == 0:
            acc = diff = 0
            for j in range(m//2):
                if grid[n//2][j] != grid[n//2][m-1-j]:
                    diff += 1
                    acc += 1
                else:
                    if grid[n//2][j] == 1:
                        acc += 2
            ans += diff
            if diff == 0 and acc % 4 == 2:
                ans += 2

        if n % 2 == 0 and m % 2 == 1:
            acc = diff = 0
            for i in range(n//2):
                if grid[i][m//2] != grid[n-1-i][m//2]:
                    diff += 1
                    acc += 1
                else:
                    if grid[i][m//2] == 1:
                        acc += 2
            ans += diff
            if diff == 0 and acc % 4 == 2:
                ans += 2

        if n % 2 == 1 and m % 2 == 1:
            if grid[n//2][m//2] == 1:
                ans += 1
            acc = diff = 0
            for j in range(m//2):
                if grid[n//2][j] != grid[n//2][m-1-j]:
                    diff += 1
                    acc += 1
                else:
                    if grid[n//2][j] == 1:
                        acc += 2

            for i in range(n//2):
                if grid[i][m//2] != grid[n-1-i][m//2]:
                    diff += 1
                    acc += 1
                else:
                    if grid[i][m//2] == 1:
                        acc += 2
            ans += diff
            if diff == 0 and acc % 4 == 2:
                ans += 2

        return ans
