class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int count = 0;
        int flag = 0;
        int count2 = 0;
        for(int i = 0; i<n/2; i++)
        {
            for(int j = 0; j<m/2; j++)
            {
                int ct1 = 0;
                if(grid[i][j] == 1)
                {
                    ct1++;
                }
                if(grid[i][m-1-j] == 1)
                {
                    ct1++;
                }
                if(grid[n-1-i][j] == 1)
                {
                    ct1++;
                }
                if(grid[n-1-i][m-1-j] == 1)
                {
                    ct1++;
                }
                count += min(ct1,4-ct1);
            }
                if(m%2 != 0)
                {
                    if(grid[i][m/2] != grid[n-1-i][m/2])
                    {
                        flag = 1;
                        count++;
                    }
                    else if(grid[i][m/2] == 1)
                    {
                        count2+=2;
                    }
                }
        }
        if(n%2 != 0)
        {
            for(int j = 0; j<m/2; j++)
            {
                if(grid[n/2][j] != grid[n/2][m-1-j])
                {
                    flag = 1;
                    count += 1;
                }
                else if(grid[n/2][j] == 1)
                {
                    count2 += 2;
                }
            }
            if(m%2 != 0 && grid[n/2][m/2] == 1)
            {
                count++;
            }
        }
        if(count2%4 != 0 && flag == 0)
        {
            count += 2;
        }
        return count;
    }
};
