class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        bool exist_odd=false;
        int cnt1=0;
        for(int i=0;i<(m+1)/2;i++){
            for(int j=0;j<(n+1)/2;j++){
                if ((m%2!=0) && (i==(m+1)/2-1)){
                    if ((n%2!=0) && (j==(n+1)/2-1)){
                        if (grid[i][j]==1) ans++;
                    }else{
                        if (grid[i][j]!=grid[i][n-1-j]){
                            ans++;
                            exist_odd=true;
                        }else{
                            if (grid[i][j]==1) cnt1+=2;
                        }
                    }
                }else{
                    if ((n%2!=0) && (j==(n+1)/2-1)){
                        if (grid[i][j]!=grid[m-1-i][j]){
                            ans++;
                            exist_odd=true;
                        }else{
                            if (grid[i][j]==1) cnt1+=2;
                        }
                    }else{
                        int c=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                        ans+=min(4-c,c);
                    }
                }
                //cout<<i<<" "<<j<<" "<<ans<<"\n";
            }
        }

        if (!exist_odd && (cnt1%4!=0)){
            ans+=2;
        }
        return ans;
    }
};
