        import java.util.*;

        class Solution {
            public int minFlips(int[][] grid) {
                int n = grid.length; int m = grid[0].length;

                int ans = 0;
                boolean flag = false;
                for(int i = 0; i < n/2; i++) {
                    for (int j = 0; j < m / 2; j++) {
                        int zero = 0;
                        int one = 0;

                        if(grid[i][j] == 0) zero++;
                        else one++;

                        if(grid[i][m-j-1] == 0) zero++;
                        else one++;

                        if(grid[n-i-1][j] == 0) zero++;
                        else one++;

                        if(grid[n-i-1][m-j-1] == 0) zero++;
                        else one++;

                        if (zero == 4 || one == 4) continue;

                        if (zero > one || one > zero) {
                            ans ++;
                        } else {
                            ans += 2;
                        }
                    }
                }
                int ones = 0;
                if (n % 2 == 1) {
                    for (int j = 0; j < m / 2; j++) {
                        if (grid[n/2][j] != grid[n/2][m-j-1]) {
                            ans += 1;
                            flag = true;

                        } else {
                            if (grid[n/2][j] == 1) {
                                ones++;
                            }
                        }
                    }
                }

                if (m % 2 == 1) {
                    for (int i = 0; i < n / 2; i++) {
                        if (grid[i][m/2] != grid[n-i-1][m/2]) {
                            ans += 1;
                            flag = true;
                        } else {
                            if (grid[i][m/2] == 1) {
                                ones++;
                            }
                        }
                    }
                }

                if (n % 2 == 1 && m % 2 == 1) {
                    if (grid[n/2][m/2] == 1) {
                        ans += 1;
                    }
                }
                if (flag) {
//                    ans += (ones%2);
                } else {
                    ans += (ones%2)*2;
                }

                return ans;
            }
        }
