class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        int minn = 0, maxx = 0;
        for (int i = 0; i <= n - 1 - i; i++) {
            for (int j = 0; j <= m - 1 - j; j++) {
                if (i < n - 1 - i && j < m - 1 - j) {
                    int cnt[2] = {};
                    cnt[grid[i][j]]++;
                    cnt[grid[i][m - 1 - j]]++;
                    cnt[grid[n - 1 - i][j]]++;
                    cnt[grid[n - 1 - i][m - 1 - j]]++;
                    ans += min(cnt[0], cnt[1]);
                } else if (i < n - 1 - i) {
                    if (grid[i][j] != grid[n - 1 - i][j]) {
                        ans++;
                        maxx += 2;
                    } else if (grid[i][j]) {
                        minn += 2;
                        maxx += 2;
                    }
                } else if (j < m - 1 - j) {
                    if (grid[i][j] != grid[i][m - 1 - j]) {
                        ans++;
                        maxx += 2;
                    } else if (grid[i][j]) {
                        minn += 2;
                        maxx += 2;
                    }
                } else {
                    minn += grid[i][j];
                    maxx += grid[i][j];
                }
            }
        }
        if (minn % 2 == 0) {
            if (minn % 4 == 0 || minn != maxx) {
                //
            } else {
                ans += 2;
            }
        } else {
            if (minn == maxx && minn % 4 == 3)
                ans += 2;
            ans++;
        }
        return ans;
    }
};
