class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        explored = set()

        rows = len(grid)
        cols = len(grid[0])

        ans = 0
        twos = []
        for row in range((rows + 1) // 2 ):
            for col in range((cols + 1) // 2 ):

                # opposite in same row
                me = (row, col)
                candidate1 = (row, cols - col - 1)
                candidate2 = (rows - row - 1, col)
                candidate3 = (rows - row - 1, cols - col - 1)

                unique = set([me, candidate1, candidate2, candidate3])
                if len(unique) == 1:
                    # can only make it 0
                    for i, j in unique:
                        if grid[i][j] != 0:
                            print("flipping", i, j)
                            ans += 1
                elif len(unique) == 4:
                    total = 0
                    for i, j in unique:
                        total += grid[i][j]
                    if total == 0 or total == 4:
                        continue
                    elif total == 1 or total == 3:
                        ans += 1
                        print("for", row, col, "flipping one")
                    else:
                        ans += 2
                        print("for", row, col, "flipping two")
                else:
                    total = 0
                    for i, j in unique:
                        total += grid[i][j]
                    if total == 0:
                        continue
                    twos.append(total)

        twos.sort()
        while len(twos) > 1 and twos[-1] == 2:
            twos.pop()
            other = twos.pop()
            ans += 4 - (2 + other)

        while len(twos) > 0:
            ans += twos.pop()


        return ans
