class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0
        for i in range(m//2):
            for j in range(n//2):
                a = grid[i][j] + grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j]
                ans += min(a, 4-a)
        k = False
        f = 0
        if m % 2 == 1:
            for j in range(n//2):
                if grid[m//2][j] != grid[m//2][n-1-j]:
                    ans += 1
                    k = True
                elif grid[m//2][j] == 1:
                    f += 1
            if n % 2 == 1:
                if grid[m//2][n//2] == 1:
                    ans += 1


        if n % 2 == 1:
            for i in range(m//2):
                if grid[i][n//2] != grid[m-1-i][n//2]:
                    ans += 1
                    k = True
                elif grid[i][n//2] == 1:
                    f += 1
        if f % 2 != 0 and not k:
            ans += 2
        return ans
