class Solution {
public:
    pair<int,int> find(vector<int> &v){
        int ans = 0 ;
        int o = 0;
        int l = 0 ;
        int r = v.size() -1 ;
        while(l<=r){
            if(v[l]==v[r]){
                if(v[l]==1){
                    o++ ;
                }
            }
            else{
                ans++ ;
            }
            l++ ;
            r-- ;
        }
        return {ans,o} ;
    }
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size() ;
        int m = grid[0].size() ;
        for(int i=0;i<grid.size();i++){
            if(n%2!=0 && i==n/2){
                continue ;
            }
            for(int j=0;j<grid[0].size();j++){
                if(m%2!=0 && j==m/2){
                    continue ;
                }
                int a = grid[i][j] ;
                int d = grid[n-1-i][j] ;
                int r = grid[i][m-1-j] ;
                int c = grid[n-1-i][m-1-j] ;

                int count = a + d + r + c ;
                ans += min(count, 4-count) ;
                grid[i][j] = 0 ;
                grid[n-1-i][j] = 0 ;
                grid[i][m-1-j] = 0 ;
                grid[n-1-i][m-1-j] = 0 ;
            }
        }
        vector<int> v = grid[n/2] ;
        vector<int> v1  ;
        for(int i=0;i<n;i++){
            v1.push_back(grid[i][m/2]) ;
        }

        if(n%2!=0){
            if(m%2==0){
                pair<int,int> p = find(v) ;
                ans+=p.first ;
                if(p.first==0){
                    if(p.second%2!=0){
                        ans+=2 ;
                    }
                }
            }   
            else{
                ans+=grid[n/2][m/2] ;
                pair<int,int> p = find(v) ;
                pair<int,int> p1 = find(v1) ;
                ans+=p.first ;
                ans+=p1.first ;
                if(p.first==0 && p1.first==0){
                    if((p.second+p1.second)%2!=0){
                        ans+=2 ;
                    }
                }
            }         
        }
        else{
            if(m%2!=0){
                pair<int,int> p = find(v1) ;
                ans+=p.first ;
                if(p.first==0){
                    if(p.second%2!=0){
                        ans+=2 ;
                    }
                }
            }
        }


        return ans ;
    }
};
