class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        if(((m==2) && (n==1)) ||(n==2 && m==1)){
            int ans = 0;
            for(int i=0;i<m;i++){
                for(int j=0;j<n;j++){
                    ans+=grid[i][j];
                }
            }
            return ans;
        }
        int ans = 0;
        for(int i=0;i<(m >> 1);i++){
            for(int j=0;j<(n >> 1); j++){
                int cnt = grid[i][n - j - 1] + grid[i][j] + grid[m - i - 1][j] + grid[m - i - 1][n - j - 1];
                if(cnt%4 == 0)continue;
                if(cnt<=2)ans+=cnt;
                else ans++;
            }
        }
        int tmp = 0;
        int oo = 0;
        if(m%2){
            int cnt = 0;
            for(int j=0;j<(n >> 1);j++){
                int a = grid[m>>1][j];
                int b = grid[m >> 1][n - j - 1];
                if(a && (a==b)){
                    tmp++;
                }
                if(a!=b)oo++;
                cnt += (grid[m >> 1][j] != grid[m >> 1][n - j - 1]);
            }
            ans += cnt;
        }
        if(n % 2){
            int cnt = 0;
            for(int j=0;j<(m >> 1);j++){
                int a = grid[j][n >> 1];
                int b = grid[m - j - 1][n >> 1];
                if(a && (a==b)){
                    tmp++;
                }
                if(a!=b)oo++;
                cnt += (grid[j][n >> 1] != grid[m - j - 1][n >> 1]);
            }
            ans += cnt;
        }
        if((m%2) && (n%2)){
            ans += grid[m>>1][n>>1];
        }
        tmp %= 2;
        if(tmp){
            if(!oo)ans+=2;
        }
        return ans;
    }
};
