class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int k=0;k<n/2;k++) {
            for(int i=0;i<m/2;i++) {
                int tmp = 0;
                tmp += grid[k][i];
                tmp += grid[n - k - 1][i];
                tmp += grid[k][m - i - 1];
                tmp += grid[n - k - 1][m - i - 1];
                if (tmp > 2) tmp = 4 - tmp;
                ans += tmp;
            }
        }
        int x = 0, y = 0, z = 0;
        if ((n % 2 == 1) && (m % 2 == 1)) {
            ans += grid[n / 2][m / 2];
        }
        if (n & 1) {
            for(int i=0;i<m/2;i++) {
                int a = grid[n / 2][i], b = grid[n / 2][m - i - 1];
                if (a != b) z++;
                else if (a == 0) {
                    x++;
                } else {
                    y++;
                }
            }
        }
        if (m & 1) {
            for(int k=0;k<n/2;k++) {
                int a = grid[k][m / 2], b = grid[n - k - 1][m / 2];
                if (a != b) z++;
                else if (a == 0) {
                    x++;
                } else {
                    y++;
                }
            }
        }
        ans += z;
        if (z == 0 && y % 2 == 1) {
            ans += 2;
        }
        return ans;
    }
};
