class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n=a.size(),m=a[0].size();
        int ans=0;
        for(int i=0;i<n-1-i;i++)
        {
            for(int j=0;j<m-1-j;j++)
            {
                int cnt=0;
                if(a[i][j])
                    cnt++;
                if(a[i][m-1-j])
                    cnt++;
                if(a[n-1-i][j])
                    cnt++;
                if(a[n-1-i][m-1-j])
                    cnt++;
                if(cnt<=2)
                {
                    ans+=cnt;
                    a[i][j]=-1;
                    a[i][m-1-j]=-1;
                    a[n-1-i][j]=-1;
                    a[n-1-i][m-1-j]=-1;
                }
                else 
                {
                    ans+=4-cnt;
                    a[i][j]=-1;
                    a[i][m-1-j]=-1;
                    a[n-1-i][j]=-1;
                    a[n-1-i][m-1-j]=-1;
                }
            }
        }
        int oo=0,zz=0,oz=0;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if(a[i][j]!=-1)
                {
                    if(i==n-1-i&&j==m-1-j)
                    {
                        ans+=a[i][j];
                        a[i][j]=-1;
                        continue;
                    }
                    if(i<n-1-i)
                    {
                        if(a[i][j]!=a[n-i-1][j])
                            oz++;
                        else if(a[i][j])
                            oo++;
                        else 
                            zz++;
                        a[i][j]=-1;
                        a[n-i-1][j]=-1;
                    }
                    else if(j<m-1-j)
                    {
                        if(a[i][j]!=a[i][m-1-j])
                            oz++;
                        else if(a[i][j])
                            oo++;
                        else 
                            zz++;
                        a[i][j]=-1;
                        a[i][m-1-j]=-1;
                    }
                }
            }
        }
        if(oz>0)
            ans+=oz;
        else 
        {
            ans+=(oo%2)*2;
        }
        return ans;
    }
};
