class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int flips = 0;
        int rows = grid.size();
        int cols = grid[0].size();
        int a = 0;
        int b = 0;

        for (int r = 0; r < (rows + 1) / 2; ++r) {
            for (int c = 0; c < (cols + 1) / 2; ++c) {
                int topLeftRow = r, topLeftCol = c;
                int bottomRightRow = rows - topLeftRow - 1, bottomRightCol = cols - topLeftCol - 1;
                int ones = 0, zeros = 0;
                if (grid[topLeftRow][topLeftCol] == 1) ++ones;
                else ++zeros;

                if (grid[topLeftRow][bottomRightCol] == 1) ++ones;
                else ++zeros;

                if (grid[bottomRightRow][topLeftCol] == 1) ++ones;
                else ++zeros;

                if (grid[bottomRightRow][bottomRightCol] == 1) ++ones;
                else ++zeros;
                if (topLeftRow == bottomRightRow && topLeftCol == bottomRightCol) {
                    if (grid[topLeftRow][topLeftCol] == 1) ++flips;
                }
                else if (topLeftRow == bottomRightRow) {
                    if (grid[topLeftRow][topLeftCol] != grid[topLeftRow][bottomRightCol]) {
                        ++flips;
                        ++b;
                    } else if (grid[topLeftRow][topLeftCol] == 1) ++a;
                }
                else if (topLeftCol == bottomRightCol) {
                    if (grid[topLeftRow][topLeftCol] != grid[bottomRightRow][topLeftCol]) {
                        ++flips;
                        ++b;
                    } else if (grid[topLeftRow][topLeftCol] == 1) ++a;
                }
                else {
                    flips += min(ones, zeros);
                }
            }
        }

        if (a % 2 == 0) {
            return flips;
        } else {
            return (b > 0) ? flips : flips + 2;
        }
    }
};
