from sortedcontainers import SortedList
from collections import defaultdict, deque, Counter
from bisect import bisect_left, bisect_right
from typing import List, Set
from itertools import pairwise


class Solution:
    def minFlips(self, a: List[List[int]]) -> int:
        n, m = len(a), len(a[0])
        cnt = [[0, 0, 0, 0, 0] for _ in range(5)]
        for i in range(n):
            for j in range(m):
                if i <= n - i - 1 and j <= m - j - 1:
                    st = {(i, j), (i, m - j - 1), (n - i - 1, j), (n - i - 1, m - j - 1)}
                    c1 = 0
                    for x, y in st:
                        c1 += a[x][y]
                    cnt[len(st)][c1] += 1
        ans = 0
        for i in range(5):
            ans += min(i, 4 - i) * cnt[4][i]
        ans += cnt[1][1]
        v = cnt[2]
        if v[1] > 0:
            ans += v[1]
        else:
            if v[2] & 1:
                ans += 2
        return ans
