class Solution {
    public int minFlips(int[][] grid) {
        int ans = 0;
        int n = grid.length , m = grid[0].length;
        for(int i = 0 ; i < n / 2; i++){
            for(int j = 0; j < m / 2; j++){
                int cnt0 = 0 ,cnt1 = 0;
                if(grid[i][j] == 0) cnt0++;
                else cnt1++;
                if(grid[i][m - j - 1] == 0) cnt0++;
                else cnt1++;
                if(grid[n - i - 1][j] == 0) cnt0++;
                else cnt1++;
                if(grid[n - i - 1][m - j - 1] == 0) cnt0++;
                else cnt1++;
                if(cnt0 >= cnt1) ans += cnt1;
                else ans += cnt0;
            }
        }
        int cnt1 = 0 , dif = 0;
        if(n % 2 != 0 ){
            for(int j = 0 ; j < m / 2; j++){
                if(grid[n / 2 ][j] != grid[n / 2][m - j - 1]) dif++;
                if(grid[n / 2][j] == 1) cnt1++;
                if(grid[n / 2][m - j - 1] == 1) cnt1++;
            }
        }
        if(m % 2 != 0){
            for(int i = 0 ; i < n / 2; i++){
                if(grid[i][m / 2] != grid[n - i - 1][m / 2]) dif++;
                if(grid[i][m / 2]  == 1) cnt1++;
                if(grid[n - i - 1][m / 2 ] == 1) cnt1++;
            }
        }
        if(n % 2 != 0 && m % 2 !=0){
            if(grid[n / 2 ][m / 2 ] == 1) cnt1++;
        }
        // System.out.println(ans);

        // System.out.println(dif);
        // System.out.println(cnt1);
        if((cnt1 + dif) % 4 == 0 ) return ans + dif;
        int p = (cnt1 + dif) % 4;
        if(dif == 0) return ans + dif + p;
        if(dif * 2 >= dif){
            if(p % 2 == 1) return ans + dif + 1;
            return ans + dif;
        }
        return ans + dif + p - dif * 2;
    }
}
