class Solution {
public:
    int ans=0,totalo=0,t=0;
    map<pair<int,int>,bool>vis;
    int minFlips(vector<vector<int>>& grid) {
        for (int i = 0; i < grid.size(); ++i) {
            for (int j = 0; j < grid[i].size(); ++j) {
                if (vis.count({i, j}))continue;
                pair<int, int> p1 = {i, j}, p2 = {i, grid[i].size() - j - 1},
                        p3 = {grid.size() - i - 1, j}, p4 = {grid.size() - i - 1, grid[i].size() - j - 1};
                set<pair<int, int>> st;
                st.insert(p1), st.insert(p2), st.insert(p3), st.insert(p4);
                if (st.size() == 4) {
                    int o = 0, z = 0;
                    for (pair<int, int> p: st) {
                        vis[p];
                        if (grid[p.first][p.second] == 1)o++;
                        else z++;
                    }
                    ans += 4 - max(o, z);
                } else if (st.size() == 2) {
                    int o = 0, z = 0;
                    for (pair<int, int> p: st) {
                        vis[p];
                        if (grid[p.first][p.second] == 1)o++;
                        else z++;
                    }
                    if (o == 2)totalo += 2;
                    else if (o)ans++,t++;
                } else {
                    int o = 0, z = 0;
                    for (pair<int, int> p: st) {
                        vis[p];
                        if (grid[p.first][p.second] == 1)o++;
                        else z++;
                    }
                    if (o)totalo++;
                }
            }

        }
        totalo%=4;
        cout<<ans<<' '<<totalo;
        if(totalo==3) {
            if (!t)
                ans += 3;
            else if (t)
                ans++;
        }
        else if(totalo==2) {
            if (!t)
                ans += 2;
        }
        else if(totalo) {
                ans++;
        }
        return ans;
    }
};
