class Solution {
    public int minFlips(int[][] g) {
        int ans = 0, m = g.length, n = g[0].length;
        int y1 = 0, y2 = n - 1;
        while(y1 < y2) {
            int x1 = 0, x2 = m - 1;
            while(x1 < x2) {
                int one = 0, zero = 0;
                if(g[x1][y1] == 1) {
                    one++;
                }
                else {
                    zero++;
                }
                if(g[x1][y2] == 1) {
                    one++;
                }
                else {
                    zero++;
                }
                if(g[x2][y1] == 1) {
                    one++;
                }
                else {
                    zero++;
                }
                if(g[x2][y2] == 1) {
                    one++;
                }
                else {
                    zero++;
                }
                ans += Math.min(one, zero);
                x1++;
                x2--;
            }
            y1++;
            y2--;
        }
        int diff = 0, same = 0, single = 0;
        if(m % 2 != 0) {
            for(int l = 0, r = n - 1; l < r; l++, r--) {
                if(g[m / 2][l] != g[m / 2][r]) {
                    diff++;
                }
                else if(g[m / 2][l] == 1){
                    same++;
                }
            }
            if(n % 2 != 0 && g[m / 2][n / 2] == 1) {
                single++;
            }
        }
        if(n % 2 != 0) {
            for(int l = 0, r = m - 1; l < r; l++, r--) {
                if(g[l][n / 2] != g[r][n / 2]) {
                    diff++;
                }
                else if(g[l][n / 2] == 1){
                    same++;
                }
            }
            if(m % 2 != 0 && g[m / 2][n / 2] == 1) {
                single++;
            }
        }
        if(single > 1) {
            single = 1;
        }
        ans += single;
        if(same % 2 == 0) {
            ans += diff;
        }
        else {
            if(diff > 0) {
                ans += diff;
            }
            else {
                ans += 2;
            }
        }
        return ans;
    }
    public static void main(String[] args) {
        Solution solution = new Solution();
        int[][] g = new int[][]{{1},{1},{1},{0}};
        solution.minFlips(g);
    }
}
