class Solution {
public:
    int f[200005][4];
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;i++) {
            for(int j=0;j<n/2;j++) {
                int z[4];
                z[0]=grid[i][j],z[1]=grid[m-i-1][j],z[2]=grid[i][n-j-1],z[3]=grid[m-i-1][n-j-1];
                int cnt=0;
                for(int k=0;k<4;k++) cnt+=z[k];
                ans+=min(cnt,4-cnt);
            }
        }
        int flex=0,is=0;
        if(m&1) {
            int mid=m/2;
            for(int j=0;j<n/2;j++) {
                if(grid[mid][j]!=grid[mid][n-j-1]) ans++,flex++;
                else if(grid[mid][j]==1) is+=2;
            }
        }
        if(n&1) {
            int mid=n/2;
            for(int i=0;i<m/2;i++) {
                if(grid[i][mid]!=grid[m-i-1][mid]) ans++,flex++;
                else if(grid[i][mid]==1) is+=2;
            }
        }
        is%=4;
        if((m&1)&&(n&1)&&grid[m/2][n/2]==1) ans++;
        if(is==2 && !flex) ans+=2;
        return ans; 
    }
};
