class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size();
        int m = g[0].size();

        int ans = 0;
        for (int i = 0; i < n / 2; i ++) {
            for (int j = 0; j < m / 2; j ++) {
                int c[2]{};
                c[g[i][j]] ++;
                c[g[n - 1 - i][j]] ++;
                c[g[i][m - 1 - j]] ++;
                c[g[n - 1 - i][m - 1 - j]] ++;
                ans += min(c[0], c[1]);
            }
        }

        if (n & 1 && m & 1) {
            ans += g[n / 2][m / 2];
        }

        int c[3]{};
        if (n & 1) {
            for (int j = 0; j < m / 2; j ++) {
                c[g[n / 2][j] + g[n / 2][m - 1 - j]] ++;
            }
        }
        if (m & 1) {
            for (int i = 0; i < n / 2; i ++) {
                c[g[i][m / 2] + g[n - 1 - i][m / 2]] ++;
            }
        }
        int s = 2 * c[2] % 4;
        ans += c[1];
        if (s == 2 && c[1] == 0) {
            ans += 2;
        }
        return ans;
    }
};
