using ll = long long ;
using ii = pair<ll,ll>;

const ll mod = 1e9+7;

#define endl "\n"
#define pb push_back
#define F first
#define S second
#define MP make_pair
#define len(x) ((ll)((x).size()))
#define rep(i,x) for(ll i=0;i<(x);i++)
#define all(x) (x).begin(),(x).end()


class Solution {
public:
    ll g=0;
    ll f1(vector<int> &a){
        ll n=len(a),i=0,j=n-1,cnt=0;
        while(i<j){
            if(a[i++]!=a[j--]){
                cnt++;
                g++;
            }
        }
        return cnt;
    }
    int minFlips(vector<vector<int>>& grid) {
        {
            ll ans=0,n=len(grid),m=len(grid[0]);
            rep(i,n/2){
                rep(j,m/2){
                    ll ctr=grid[i][j]+
                        grid[i][m-j-1]+
                        grid[n-i-1][j]+
                        grid[n-i-1][m-j-1];
                    ctr=min(ctr,4-ctr);
                    ans+=ctr;
                }
            }
            ll ctr=0,kk=0;
            bool all1=1,all2=1;
            if((n&1) && (m&1)){
                ctr+=grid[n/2][m/2];
            }
            if(m&1){
                rep(i,n/2){
                    if(grid[i][m/2] != grid[n-i-1][m/2]){
                        ctr++;
                        all2=0;
                    }else{
                        kk+=grid[i][m/2]*2;
                    }
                }
                if(all2==0){
                    kk=0;
                }
            }

            if(n&1){
                rep(j,m/2){
                    if(grid[n/2][m-j-1] != grid[n/2][j] ){
                        ctr++;
                        all1=0;
                    }else{
                        kk+=grid[n/2][j]*2;
                    }

                }
                if(all1==0){
                    kk=0;
                }

            }

            if(all1 && all2){
                ans+= (kk%4);
            }

            ans+=ctr;

            return ans;
        }

        ll o=0,m=0;

        for(auto x:grid){
            if(len(x)&1){
                m+=x[len(x)/2];
            }
            for(auto y:x)o+=y;
        }
        o-=m;
        cout<<o<<" "<<m<<endl;
        ll ans=1e18,cnt=0;

        for(auto x:grid){
            cnt+=f1(x);
        }

        ans=cnt;

        // o+ans , o-ans +m
        ll tmp=min((m+o+ans)%4,(m+o-ans)%4);
        ans+=tmp;

        o+=m;
        m=0;
        cnt=0;
        for(ll j=0;j<len(grid[0]);j++){
            ll p=0,q=len(grid)-1;
            while(p<q){
                if(grid[p++][j]!=grid[q--][j]){
                    cnt++;
                }
            }
            if(len(grid)&1){
                m+=grid[len(grid)/2][j];
            }

        }
        o-=m;
        cout<<o<<" "<<m<<endl;
         tmp=min((m+o+cnt)%4,(m+o-cnt)%4);
        cnt+=tmp;
        return min(ans,cnt);
    }
};
