class Solution {
public:
    int minFlips(vector<vector<int>>&v)
    {
        int n=v.size(),m=v[0].size(),ans=0;

        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int ct=v[i][j]+v[n-i-1][j]+v[i][m-j-1]+v[n-i-1][m-j-1];
                ans+=min(ct,4-ct);
            }
        }

        int total_ones=0,total_diff=0,total_ct=0;

        if(n&1)
        {
            for(int j=0;j<m/2;j++)
            {
                total_diff+=v[n/2][j]!=v[n/2][m-j-1];
                total_ones+=2*(v[n/2][j]&v[n/2][m-j-1]);
                total_ct+=2;
            }
        }

        if(m&1)
        {
            for(int i=0;i<n/2;i++)
            {
                total_diff+=v[i][m/2]!=v[n-i-1][m/2];
                total_ones+=2*(v[i][m/2]&v[n-i-1][m/2]);
                total_ct+=2;
            }
        }

        if((n&1)&(m&1))
        {
            ans+=v[n/2][m/2];
        }

        total_ones%=4;


        if(total_ones==2)
        {
            if(total_diff)      total_diff--,ans++;
            else                ans+=2;
        }


        return ans+total_diff;
    }
};
