class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        res = 0
        for i in range(m//2):
            for j in range(n//2):
                tmp = grid[i][j] + grid[m-1-i][j] + grid[i][n-1-j] + grid[m-1-i][n-1-j]
                res += min(tmp, 4 - tmp)
        if m%2 == 0 and n%2 == 0:
            return res
        if m%2 == 1 and n%2 == 0:
            arr = grid[m//2]
            tmp = 0
            total = 0
            for i in range(n//2):
                if arr[i] != arr[n-1-i]:
                    tmp += 1
                total += (arr[i]+arr[n-1-i])
            if tmp == 0 and total % 4 == 2:
                tmp += 2
            return res + tmp
        if m%2 == 0 and n%2 == 1:
            arr = list(zip(*grid))[n//2]
            tmp = 0
            total = 0
            for i in range(m//2):
                if arr[i] != arr[m-1-i]:
                    tmp += 1
                total += (arr[i]+arr[m-1-i])
            if tmp == 0 and total % 4 == 2:
                tmp += 2
            return res + tmp       
        if m%2 == 1 and n%2 == 1:
            arr1 = grid[m//2]   
            tmp = 0
            total = 0
            for i in range(n//2):
                if arr1[i] != arr1[n-1-i]:
                    tmp += 1
                total += (arr1[i]+arr1[n-1-i])
            arr2 = list(zip(*grid))[n//2]
            for i in range(m//2):
                if arr2[i] != arr2[m-1-i]:
                    tmp += 1
                total += (arr2[i]+arr2[m-1-i])
            #print(arr1,arr2,tmp,total)    
            if tmp == 0 and total % 4 == 2:
                tmp += 2

            if grid[m//2][n//2] == 1:
                tmp += 1
            return res + tmp
