class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n=v.size(),m=v[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int x1,y1,x2,y2,x3,y3,x4,y4;
                x1=x3=i;
                x2=x4=n-i-1;
                y1=y2=j;
                y3=y4=m-j-1;
                int one=v[x1][y1]+v[x2][y2]+v[x3][y3]+v[x4][y4];
                one=min(one,4-one);
                ans+=one;
            }
        }
        int one=0,k=0;
        if(n%2){
            int x=n/2;
            for(int j=0;j<m/2;j++){
                if(v[x][j]!=v[x][m-j-1]) k++;
                else if(v[x][j]==1) one+=2;
            }
        }
        if(m%2){
            int x=m/2;
            for(int j=0;j<n/2;j++){
                if(v[j][x]!=v[n-j-1][x]) k++;
                else if(v[j][x]==1) one+=2;
            }
        }
        one%=4;
        // cout<<one<<" "<<k<<endl;
        if(one){
            if(k){
                ans+=k;
            }else ans+=2;
        }else ans+=k;
        if(n%2&&m%2){
            ans+=v[n/2][m/2];
        }
        return ans;
    }
};
