class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n=v.size(),m=v[0].size(),ans=0,i,j;
        if(n==1&&m==1){
            if(v[0][0]==1) return 1;
            else return 0;
        }
        for(i=0;i<n/2;i++){
            for(j=0;j<m/2;j++){
                int a=v[i][j]+v[n-i-1][j]+v[i][m-j-1]+v[n-i-1][m-j-1];
                ans+=min(a,4-a);
            }
        }
        int flag1=0,flag2=0,v11=0,v12=0,v21=0,v22=0,mid1=(n/2),mid2=(m/2);
        // cout<<ans<<"\n";
        if(n%2!=0){
            flag1=1;
            v12=INT_MAX-10;
            for(j=0;j<m/2;j++){
                int a=v[mid1][j]+v[mid1][m-j-1];
                int temp=v11;
                v11=min(v11+a,v12+2-a);
                v12=min(temp+2-a,v12+a);
            }
        }
        if(m%2!=0){
            flag2=1;
            v22=INT_MAX-10;
            for(i=0;i<n/2;i++){
                int a=v[i][mid2]+v[n-i-1][mid2];
                int temp=v21;
                v21=min(v21+a,v22+2-a);
                v22=min(temp+2-a,v22+a);
            }
        }
        // cout<<v11<<" "<<v22<<"\n";
        if(flag1==1&&flag2==1){
            ans+=min(v11+v21,v12+v22);
            if(v[mid1][mid2]==1) ans++;
        }
        else if(flag1==1){
            ans+=v11;
        }
        else ans+=v21;
        return ans;
    }
};
