class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();

        int ans=0;

        for (int i=0;i<n/2;i++)
            for (int j=0;j<m/2;j++) {
                int I=n-i-1;
                int J=m-j-1;
                int s = grid[i][j]+grid[I][j]+grid[i][J]+grid[I][J];
                ans+=min(s,4-s);
            }


        if ((n%2) && (m%2) && grid[n/2][m/2]==1) {
            ans++;
            grid[n/2][m/2]=0;
        }

        int all2=0;
        int ops=0;

        if (n%2) {
            for (int j=0;j<m/2;j++) {
                int s=grid[n/2][j]+grid[n/2][m-1-j];
                if (s==2) all2++;
                ops+=min(s,2-s);
            }
        };

        if (m%2) {
            for (int i=0;i<n/2;i++) {
                int s=grid[i][m/2]+grid[n-1-i][m/2];
                if (s==2) all2++;
                ops+=min(s,2-s);
            }
        };

        ans+=ops;

        if (all2 % 2) {
            if (ops==0)
                ans+=2;
        };
        return ans;
    }
};
