class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n = len(grid),len(grid[0])
        cnt = 0
        one = 0
        two = 0
        for i in range(m//2):
            for j in range(n//2):
                tmp = grid[i][j] + grid[i][n-1-j]+grid[m-1-i][j] + grid[m-1-i][n-1-j]
                if tmp ==1 or tmp ==3:
                    cnt += 1
                elif tmp == 2:
                    cnt += 2
                    two += 1
        res = 0
        if m &1 :
            for j in range(n//2):
                if grid[m//2][j] != grid[m//2][n-1-j]:
                    cnt += 1
                    one += 1
                else:
                    res += grid[m//2][j]*2
        if n &1:
            for i in range(m>>1):
                if grid[i][n//2] != grid[m-1-i][n//2]:
                    cnt += 1
                    one += 1
                else:
                    res += grid[i][n//2]*2

        if m&1 and n&1:
            res += grid[m//2][n//2]
        res %= 4
        print(res,one,two)
        if res == 1:
            return cnt + 1
        if res == 2:
            if one:
                return cnt
            else:
                return cnt + 2
        if res == 3:
            if one :
                return cnt + 1
            else:
                return cnt +3
        return cnt
