class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size(), ans = 0;
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int k = a[i][j] + a[n - 1 - i][j] + a[i][m - 1 - j] + a[n - 1 - i][m - 1 - j];
                ans += min(k, 4 - k);
            }
        }
        int x = 0, y = 0, z = 0;
        if (m % 2 == 1) {
            for (int i = 0; i < n / 2; i++) {
                x += a[i][m / 2] ^ a[n - 1 - i][m / 2];
            }
            for (int i = 0; i < n; i++) z += a[i][m / 2];
        }
        if (n % 2 == 1) {
            for (int j = 0; j < m / 2; j++) {
                y += a[n / 2][j] ^ a[n / 2][m - 1 - j];
            }
            for (int j = 0; j < m; j++) z += a[n / 2][j];
        }
        if (n % 2 == 1 && m % 2 == 1) {
            z -= 2 * a[n / 2][m / 2];
            if (a[n / 2][m / 2]) ans++;
        }
        if (x + y == 0 && z % 4 == 2) ans += 2;
        ans += x + y;
        return ans;
    }
};
