#include <bits/stdc++.h>
using namespace std;
class Solution {
public:
    int minFlips(vector<vector<int>> &grid) {
        int m = grid.size(), n = grid.front().size();
        int ans = INT_MAX, cur = 0;
        for (int i = 0; i < m / 2; ++i) {
            for (int j = 0; j < n / 2; ++j) {
                int c = grid[i][j] + grid[m - 1 - i][j] + grid[i][n - 1 - j]
                        + grid[m - 1 - i][n - 1 - j];
                cur += min(c, 4 - c);
            }
        }
        if (m % 2 == 1 && n % 2 == 1) {
            cur += grid[m / 2][n / 2];
            int c = 0, k = 0;
            for (int i = 0; i < m / 2; ++i) {
                if (grid[i][n/2] != grid[m-1-i][n/2]) {
                    k++;
                }
                c += grid[i][n / 2] + grid[m - 1 - i][n / 2];
            }
            for (int j = 0; j < n / 2; ++j) {
                if (grid[m / 2][j] != grid[m / 2][n - 1 - j]) {
                    k++;
                }
                c += grid[m / 2][j] + grid[m / 2][n - 1 - j];
            }
            int a = (c - k) % 4, b = (c + k) % 4;
            if (k != 0) {
                cur += k;
            } else {
                cur += c % 4;
            }
        } else if (m % 2 == 1) {
            int c = 0, k = 0;
            for (int j = 0; j < n / 2; ++j) {
                if (grid[m / 2][j] != grid[m / 2][n - 1 - j]) {
                    k++;
                }
                c += grid[m / 2][j] + grid[m / 2][n - 1 - j];
            }
            int a = (c - k) % 4, b = (c + k) % 4;
            if (k != 0) {
                cur += k;
            } else {
                cur += c % 4;
            }
        } else if (n % 2 == 1) {
            int c = 0, k = 0;
            for (int i = 0; i < m / 2; ++i) {
                if (grid[i][n/2] != grid[m-1-i][n/2]) {
                    k++;
                }
                c += grid[i][n / 2] + grid[m - 1 - i][n / 2];
            }
            int a = (c - k) % 4, b = (c + k) % 4;
            if (k != 0) {
                cur += k;
            } else {
                cur += c % 4;
            }
        }
        ans = cur;
        return ans;
    }
};
